import React, { Component } from "react";
import Joi from 'joi-browser';
import Input from "./input";
class Form extends Component {
    state = {
        data:{},
        errors:{}
    }
    validate = ()=>{
        const options={abortEarly:false};
        const {error} = Joi.validate(this.state.data,this.schema, options);
        //By default: Joi will stop validate when one field incorrect.
        //To validate all field use option {abortEarly:false}
        if(!error) return null;
        const errors = {};
        for(let item of error.details)
            errors[item.path[0]]= item.message;
        return errors;
    }
    validateProperty =({name,value})=>{
        const obj={[name]:value};
        const schema = {[name]:this.schema[name]};
        const {error} = Joi.validate(obj,schema);
        return error?error.details[0].message:null;
    }
    handleSubmit = e => {
        e.preventDefault();//prevent submit and  prevent re-load the whole page
        const errors = this.validate();
        this.setState({errors:errors||{}});
        if(errors) return;
        this.doSubmit();
    }
    handleChange = ({currentTarget:input}) =>{
        const errors = {...this.state.errors};
        const errorMessage = this.validateProperty(input);
        if(errorMessage) errors[input.name]=errorMessage;
        else delete errors[input.name];
        const data = {...this.state.data};
        data[input.name] = input.value;
        this.setState({data,errors});
    }
    renderInput(name,label){
        return(
            <Input
                name="username"
                label="Username"
                value={data.username}
                onChange={this.handleChange}
                error={errors.username}
            />
        )
    }

    renderButton(label){
        return (<button disabled={this.validate()} className="btn btn-primary">{label}</button>)
    }
}
export default Form;
