import React, {Component} from "react";
import Input from './common/input';
import Joi from 'joi-browser';
class LoginForm extends Component{
    state = {
        account:{username:"", password:""},
        errors:{}
    }
    schema = {
        username:Joi.string().required(),
        password:Joi.string().required(),
    }
    validate = ()=>{
        const result = Joi.validate(this.state.account,this.schema, {abortEarly:false});
        //By default: Joi will stop validate when one field incorrect.
        //To validate all field use option {abortEarly:false}
        if(!result.errors) return null;
        const errors = {};
        for(let item of result.errors.details)
            errors[item.path[0]]= item.message;

    }
    handleSubmit = e => {
        e.preventDefault();//prevent submit and  prevent re-load the whole page
        //Call server
        //Redirect
        const errors = this.validate();
        this.setState({errors:errors||{}});
        if(errors) return;
        console.log('Submitted');

    }
    validateProperty =({name,value})=>{
        if(name==='username'){
            if(value.trim()==='') return 'User name is required.';
            if(value.trim()==='a') return 'aaaaa';
        }
        if(name==='password'){
            if(value.trim()==='') return 'Password is required.';
        }
    }
    handleChange = ({currentTarget:input}) =>{
        const errors = {...this.state.errors};
        const errorMessage = this.validateProperty(input);
        if(errorMessage) errors[input.name]=errorMessage;
        else delete errors[input.name];

        const account = {...this.state.account};
        account[input.name] = input.value;

        this.setState({account,errors});
    }
    render() {
        const {account,errors} = this.state;
        return (
            <div>
                <h1>Login</h1>
                <form onSubmit={this.handleSubmit}>
                    <Input
                        name="username"
                        label="Username"
                        value={account.username}
                        onChange={this.handleChange}
                        error={errors.username}
                    />
                    <Input
                        name="password"
                        label="Password"
                        value={account.password}
                        onChange={this.handleChange}
                        error={errors.password}
                    />
                    <button className="btn btn-primary">Login</button>
                </form>
            </div>
        )
    }
};

export default LoginForm;
